    // Helper function to get all emojis as a flat list for search
    fun getAllEmojis(): List<String> {
        return emojiCategories.flatMap { it.second }
    }

    // Helper function to search emojis
    fun searchEmojis(query: String): List<String> {
        if (query.isBlank()) return emptyList()
        
        val allEmojis = getAllEmojis()
        val queryLower = query.lowercase()
        
        // Simple search - in a real app you might want more sophisticated search
        // For now, we'll just return emojis from categories that match the query
        return when {
            queryLower.contains("happy") || queryLower.contains("smile") -> emojiCategories[0].second.filter { it in listOf("ğŸ˜€", "ğŸ˜ƒ", "ğŸ˜„", "ğŸ˜Š", "ğŸ˜", "ğŸ™‚", "ğŸ˜Œ", "ğŸ˜†", "ğŸ¤£", "ğŸ˜‚") }
            queryLower.contains("sad") || queryLower.contains("cry") -> emojiCategories[0].second.filter { it in listOf("ğŸ˜¢", "ğŸ˜­", "ğŸ˜", "ğŸ˜”", "ğŸ˜Ÿ", "ğŸ¥º", "ğŸ˜¿", "ğŸ˜¾") }
            queryLower.contains("love") || queryLower.contains("heart") -> emojiCategories.flatMap { it.second }.filter { it in listOf("â¤ï¸", "ğŸ§¡", "ğŸ’›", "ğŸ’š", "ğŸ’™", "ğŸ’œ", "ğŸ–¤", "ğŸ¤", "ğŸ’•", "ğŸ’–", "ğŸ’—", "ğŸ’˜", "ğŸ’", "ğŸ’", "ğŸ’Ÿ", "ğŸ˜", "ğŸ¥°", "ğŸ˜˜") }
            queryLower.contains("fire") || queryLower.contains("lit") -> listOf("ğŸ”¥", "âœ¨", "ğŸ’¯", "ğŸŒŸ", "â­")
            queryLower.contains("food") -> emojiCategories.find { it.first == "Food & Drink" }?.second?.take(20) ?: emptyList()
            queryLower.contains("animal") -> emojiCategories.find { it.first == "Animals & Nature" }?.second?.take(20) ?: emptyList()
            queryLower.contains("travel") -> emojiCategories.find { it.first == "Travel & Places" }?.second?.take(20) ?: emptyList()
            else -> {
                // Default search across all categories
                allEmojis.take(20)
